window.onload = function () {

    var urlToGo = "http://gamenpartners.com/click.php?lp=1";


    var myVideo = document.getElementById("video_SlotoKing"),
        btnBaks = document.getElementsByClassName("btn_baks"),
        searchFild = document.getElementsByClassName("bl_search")[0];

    function elementToClick(btn) {
        btn.addEventListener('click', goToLink);
    }
    elementToClick(searchFild);

    for (var i = 0; i < btnBaks.length; i++) {
        btnBaks[i].addEventListener('click', goToLink);
    }

    function goToLink() {
        window.location.href = urlToGo;
    }



    myVideo.addEventListener("canplay", function() {
        setTimeout(function() {
            myVideo.play();
        }, 1500);
    });

};


